function renderProducts(){const grid=document.getElementById('productGrid');const t=translations[currentLang];grid.innerHTML=productsData.map(p=>`<div class="product-card" data-id="${p.id}"><div class="product-image"><img src="${p.img}" alt="${p.name}" loading="lazy"><span class="product-badge">${p.badge}</span><button class="product-wishlist" aria-label="Add to wishlist">‚ô°</button></div><div class="product-info"><h3 class="product-name">${p.name}</h3><p class="product-category">${p.category}</p><div class="product-price"><span class="current-price">‚Çπ${p.price.toLocaleString()}</span><span class="original-price">‚Çπ${p.originalPrice.toLocaleString()}</span></div><div class="product-actions"><button class="add-cart-btn" data-id="${p.id}" data-translate="addToCart">${t.addToCart}</button><button class="whatsapp-btn" data-id="${p.id}" title="Order on WhatsApp">üí¨</button></div></div></div>`).join('');}
function renderFeatures(){const grid=document.getElementById('featuresGrid');const t=translations[currentLang];grid.innerHTML=featuresData.map(f=>`<div class="feature-card"><div class="feature-icon">${f.icon}</div><h3 data-translate="${f.name}">${t[f.name]}</h3><p data-translate="${f.desc}">${t[f.desc]}</p></div>`).join('');}
function initOfferTimer(){const timer=document.getElementById('offerTimer');const endDate=new Date();endDate.setDate(endDate.getDate()+3);function update(){const now=new Date();const diff=endDate-now;if(diff<=0)return;const d=Math.floor(diff/(1000*60*60*24));const h=Math.floor((diff%(1000*60*60*24))/(1000*60*60));const m=Math.floor((diff%(1000*60*60))/(1000*60));const s=Math.floor((diff%(1000*60))/1000);timer.innerHTML=`<div class="timer-box"><span>${d}</span><small>Days</small></div><div class="timer-box"><span>${h}</span><small>Hours</small></div><div class="timer-box"><span>${m}</span><small>Mins</small></div><div class="timer-box"><span>${s}</span><small>Secs</small></div>`;}update();setInterval(update,1000);}
function initEventListeners(){document.getElementById('themeToggle').addEventListener('click',toggleTheme);document.querySelectorAll('.lang-btn').forEach(b=>b.addEventListener('click',()=>switchLanguage(b.dataset.lang)));document.getElementById('cartBtn').addEventListener('click',()=>toggleCart(true));document.getElementById('cartClose').addEventListener('click',()=>toggleCart(false));document.getElementById('overlay').addEventListener('click',closeAllModals);document.getElementById('checkoutBtn').addEventListener('click',openCheckout);document.getElementById('checkoutClose').addEventListener('click',()=>toggleModal('checkoutModal',false));document.getElementById('viewInvoiceBtn').addEventListener('click',showInvoice);document.getElementById('continueShopping').addEventListener('click',()=>{toggleModal('successModal',false);location.reload();});document.getElementById('printInvoice').addEventListener('click',printInvoice);document.getElementById('closeInvoice').addEventListener('click',()=>toggleModal('invoiceModal',false));document.getElementById('mobileMenuBtn').addEventListener('click',toggleMobileMenu);document.getElementById('newsletterForm').addEventListener('submit',e=>{e.preventDefault();alert('Thank you for subscribing!');e.target.reset();});document.getElementById('productGrid').addEventListener('click',handleProductClick);document.getElementById('cartItems').addEventListener('click',handleCartAction);window.addEventListener('scroll',handleScroll);document.querySelectorAll('a[href^="#"]').forEach(a=>a.addEventListener('click',smoothScroll));}
function toggleTheme(){const isDark=document.body.getAttribute('data-theme')==='dark';document.body.setAttribute('data-theme',isDark?'':'dark');localStorage.setItem('raviAnnaTheme',isDark?'light':'dark');}
function switchLanguage(lang){currentLang=lang;localStorage.setItem('raviAnnaLang',lang);document.querySelectorAll('.lang-btn').forEach(b=>b.classList.toggle('active',b.dataset.lang===lang));translatePage();renderCategories();renderProducts();renderFeatures();}
function toggleCart(open){document.getElementById('cartSidebar').classList.toggle('open',open);document.getElementById('overlay').classList.toggle('open',open);if(open)renderCartItems();}
function toggleModal(id,open){document.getElementById(id).classList.toggle('open',open);document.getElementById('overlay').classList.toggle('open',open);}
function closeAllModals(){toggleCart(false);toggleModal('checkoutModal',false);toggleModal('successModal',false);toggleModal('invoiceModal',false);}
function toggleMobileMenu(){document.getElementById('nav').classList.toggle('open');const btn=document.getElementById('mobileMenuBtn');btn.classList.toggle('active');}
function handleScroll(){const header=document.getElementById('header');header.classList.toggle('scrolled',window.scrollY>50);}
function smoothScroll(e){e.preventDefault();const target=document.querySelector(this.getAttribute('href'));if(target)target.scrollIntoView({behavior:'smooth'});document.getElementById('nav').classList.remove('open');}
function handleProductClick(e){const addBtn=e.target.closest('.add-cart-btn');const waBtn=e.target.closest('.whatsapp-btn');const wishBtn=e.target.closest('.product-wishlist');if(addBtn)addToCart(parseInt(addBtn.dataset.id));if(waBtn)openWhatsApp(parseInt(waBtn.dataset.id));if(wishBtn){wishBtn.classList.toggle('active');wishBtn.textContent=wishBtn.classList.contains('active')?'‚ô•':'‚ô°';}}
function addToCart(id){const product=productsData.find(p=>p.id===id);if(!product)return;const existing=cart.find(i=>i.id===id);if(existing)existing.qty++;else cart.push({...product,qty:1});saveCart();updateCartUI();animateCartCount();}
function saveCart(){localStorage.setItem('raviAnnaCart',JSON.stringify(cart));}
function updateCartUI(){const count=cart.reduce((sum,i)=>sum+i.qty,0);document.getElementById('cartCount').textContent=count;const total=cart.reduce((sum,i)=>sum+i.price*i.qty,0);document.getElementById('cartTotal').textContent=`‚Çπ${total.toLocaleString()}`;}
function animateCartCount(){const el=document.getElementById('cartCount');el.classList.add('bump');setTimeout(()=>el.classList.remove('bump'),300);}
function renderCartItems(){const container=document.getElementById('cartItems');const t=translations[currentLang];if(cart.length===0){container.innerHTML=`<div class="cart-empty"><div class="cart-empty-icon">üõí</div><p>Your cart is empty</p></div>`;return;}container.innerHTML=cart.map(i=>`<div class="cart-item" data-id="${i.id}"><div class="cart-item-image"><img src="${i.img}" alt="${i.name}"></div><div class="cart-item-details"><div class="cart-item-name">${i.name}</div><div class="cart-item-price">‚Çπ${i.price.toLocaleString()}</div><div class="cart-item-qty"><button class="qty-btn minus" data-id="${i.id}">‚àí</button><span>${i.qty}</span><button class="qty-btn plus" data-id="${i.id}">+</button></div></div><button class="cart-item-remove" data-id="${i.id}">üóëÔ∏è</button></div>`).join('');}
function handleCartAction(e){const id=parseInt(e.target.dataset.id);if(e.target.classList.contains('minus')){const item=cart.find(i=>i.id===id);if(item&&item.qty>1)item.qty--;else cart=cart.filter(i=>i.id!==id);}else if(e.target.classList.contains('plus')){const item=cart.find(i=>i.id===id);if(item)item.qty++;}else if(e.target.classList.contains('cart-item-remove')){cart=cart.filter(i=>i.id!==id);}saveCart();updateCartUI();renderCartItems();}
function openWhatsApp(id){const product=productsData.find(p=>p.id===id);if(!product)return;const cartItem=cart.find(i=>i.id===id);const qty=cartItem?cartItem.qty:1;const msg=encodeURIComponent(`Hi! I'm interested in ordering:\n\n*${product.name}*\nPrice: ‚Çπ${product.price.toLocaleString()}\nQuantity: ${qty}\n\nPlease confirm availability.`);window.open(`https://wa.me/919876543210?text=${msg}`,'_blank');}
function openCheckout(){toggleCart(false);toggleModal('checkoutModal',true);renderCheckoutStep(1);}
function renderCheckoutStep(step){const content=document.getElementById('checkoutContent');const t=translations[currentLang];document.querySelectorAll('.step').forEach((s,i)=>{s.classList.toggle('active',i+1===step);s.classList.toggle('completed',i+1<step);});const subtotal=cart.reduce((sum,i)=>sum+i.price*i.qty,0);const shipping=subtotal>=999?0:50;const total=subtotal+shipping;if(step===1){content.innerHTML=`<h3 data-translate="reviewCart">${t.reviewCart}</h3><div class="checkout-items">${cart.map(i=>`<div class="checkout-item"><span>${i.name} √ó ${i.qty}</span><span>‚Çπ${(i.price*i.qty).toLocaleString()}</span></div>`).join('')}</div><div class="checkout-summary"><div class="summary-row"><span data-translate="subtotal">${t.subtotal}</span><span>‚Çπ${subtotal.toLocaleString()}</span></div><div class="summary-row"><span data-translate="shipping">${t.shipping}</span><span>${shipping===0?'Free':`‚Çπ${shipping}`}</span></div><div class="summary-row total"><span data-translate="total">${t.total}</span><span>‚Çπ${total.toLocaleString()}</span></div></div><button class="btn btn-primary btn-full" id="toStep2" data-translate="continueToDetails">${t.continueToDetails}</button>`;document.getElementById('toStep2').addEventListener('click',()=>renderCheckoutStep(2));}else if(step===2){content.innerHTML=`<h3 data-translate="customerDetails">${t.customerDetails}</h3><form class="checkout-form" id="detailsForm"><div class="form-group"><label data-translate="fullName">${t.fullName}</label><input type="text" id="customerName" required></div><div class="form-group"><label data-translate="phoneNumber">${t.phoneNumber}</label><input type="tel" id="customerPhone" required></div><div class="form-group"><label data-translate="email">${t.email}</label><input type="email" id="customerEmail" required></div><div class="form-group"><label data-translate="address">${t.address}</label><textarea id="customerAddress" rows="3" required></textarea></div><div class="form-group"><label data-translate="pincode">${t.pincode}</label><input type="text" id="customerPincode" required></div><button type="submit" class="btn btn-primary btn-full" data-translate="continueToPayment">${t.continueToPayment}</button></form>`;document.getElementById('detailsForm').addEventListener('submit',e=>{e.preventDefault();customerData={name:document.getElementById('customerName').value,phone:document.getElementById('customerPhone').value,email:document.getElementById('customerEmail').value,address:document.getElementById('customerAddress').value,pincode:document.getElementById('customerPincode').value};renderCheckoutStep(3);});}else if(step===3){content.innerHTML=`<h3 data-translate="selectPayment">${t.selectPayment}</h3><div class="payment-methods"><label class="payment-option"><input type="radio" name="payment" value="upi" checked><div class="payment-card"><span class="payment-icon">üì±</span><div><strong>UPI</strong><p>Google Pay, PhonePe, Paytm</p></div></div></label><label class="payment-option"><input type="radio" name="payment" value="card"><div class="payment-card"><span class="payment-icon">üí≥</span><div><strong data-translate="creditDebit">${t.creditDebit}</strong><p>Visa, Mastercard, RuPay</p></div></div></label><label class="payment-option"><input type="radio" name="payment" value="cod"><div class="payment-card"><span class="payment-icon">üíµ</span><div><strong data-translate="cod">${t.cod}</strong><p data-translate="codDesc">${t.codDesc}</p></div></div></label></div><div class="payment-details"><div class="upi-details active" id="upiDetails"><div class="form-group"><label>UPI ID</label><input type="text" placeholder="yourname@upi" id="upiId"></div></div><div class="card-details" id="cardDetails"><div class="form-group"><label data-translate="cardNumber">${t.cardNumber}</label><input type="text" placeholder="1234 5678 9012 3456" id="cardNumber"></div><div class="form-row"><div class="form-group"><label data-translate="expiry">${t.expiry}</label><input type="text" placeholder="MM/YY" id="cardExpiry"></div><div class="form-group"><label>CVV</label><input type="text" placeholder="123" id="cardCvv"></div></div></div></div><div class="order-summary-final"><div class="summary-row total"><span data-translate="totalPayable">${t.totalPayable}</span><span>‚Çπ${total.toLocaleString()}</span></div></div><button class="btn btn-primary btn-full" id="payNowBtn" data-translate="payNow">${t.payNow}</button>`;document.querySelectorAll('input[name="payment"]').forEach(r=>{r.addEventListener('change',()=>{document.getElementById('upiDetails').classList.toggle('active',r.value==='upi');document.getElementById('cardDetails').classList.toggle('active',r.value==='card');});});document.getElementById('payNowBtn').addEventListener('click',processPayment);}}
function processPayment(){toggleModal('checkoutModal',false);setTimeout(()=>{toggleModal('successModal',true);},300);}
function showInvoice(){toggleModal('successModal',false);const paper=document.getElementById('invoicePaper');const t=translations[currentLang];const orderId='RA'+Date.now().toString().slice(-8);const date=new Date().toLocaleDateString('en-IN',{day:'2-digit',month:'short',year:'numeric'});const subtotal=cart.reduce((sum,i)=>sum+i.price*i.qty,0);const shipping=subtotal>=999?0:50;const total=subtotal+shipping;paper.innerHTML=`<div class="invoice-header"><div class="invoice-logo"><span class="logo-icon">‚ú¶</span><span class="logo-text">Ravi Anna</span></div><div class="invoice-title">INVOICE</div></div><div class="invoice-info"><div class="invoice-details"><p><strong data-translate="orderId">${t.orderId}</strong> ${orderId}</p><p><strong data-translate="date">${t.date}</strong> ${date}</p></div><div class="invoice-customer"><p><strong data-translate="billTo">${t.billTo}</strong></p><p>${customerData.name||'Customer'}</p><p>${customerData.address||''}</p><p>${customerData.phone||''}</p></div></div><table class="invoice-table"><thead><tr><th data-translate="item">${t.item}</th><th data-translate="qty">${t.qty}</th><th data-translate="price">${t.price}</th><th data-translate="amount">${t.amount}</th></tr></thead><tbody>${cart.map(i=>`<tr><td>${i.name}</td><td>${i.qty}</td><td>‚Çπ${i.price.toLocaleString()}</td><td>‚Çπ${(i.price*i.qty).toLocaleString()}</td></tr>`).join('')}</tbody></table><div class="invoice-totals"><div class="invoice-total-row"><span data-translate="subtotal">${t.subtotal}</span><span>‚Çπ${subtotal.toLocaleString()}</span></div><div class="invoice-total-row"><span data-translate="shipping">${t.shipping}</span><span>${shipping===0?'Free':`‚Çπ${shipping}`}</span></div><div class="invoice-total-row grand-total"><span data-translate="grandTotal">${t.grandTotal}</span><span>‚Çπ${total.toLocaleString()}</span></div></div><div class="invoice-footer"><p data-translate="invoiceThankYou">${t.invoiceThankYou}</p><p>www.ravianna.com | +91 98765 43210</p></div>`;toggleModal('invoiceModal',true);cart=[];saveCart();updateCartUI();}
function printInvoice(){const content=document.getElementById('invoicePaper').innerHTML;const win=window.open('','','width=600,height=800');win.document.write(`<!DOCTYPE html><html><head><title>Invoice - Ravi Anna</title><style>body{font-family:Arial,sans-serif;padding:20px;color:#333}.invoice-header{display:flex;justify-content:space-between;align-items:center;border-bottom:2px solid #c9a85c;padding-bottom:15px;margin-bottom:20px}.logo-icon{color:#c9a85c;font-size:24px;margin-right:8px}.logo-text{font-size:22px;font-weight:bold}.invoice-title{font-size:24px;font-weight:bold;color:#c9a85c}.invoice-info{display:flex;justify-content:space-between;margin-bottom:20px}.invoice-table{width:100%;border-collapse:collapse;margin:20px 0}.invoice-table th,.invoice-table td{padding:10px;border-bottom:1px solid #ddd;text-align:left}.invoice-table th{background:#f5f5f5}.invoice-totals{margin-top:20px}.invoice-total-row{display:flex;justify-content:space-between;padding:5px 0}.grand-total{font-size:18px;font-weight:bold;color:#c9a85c;border-top:2px dashed #c9a85c;margin-top:10px;padding-top:10px}.invoice-footer{text-align:center;margin-top:30px;padding-top:20px;border-top:1px solid #ddd;color:#666}</style></head><body>${content}</body></html>`);win.document.close();win.print();}
